import React, { useEffect, useState } from 'react';

//const data = 'https://first-express-project.herokuapp.com/movies';

const API = 'https://first-express-project.herokuapp.com/movies';

export const Movie = () => {
  const [movies, setMovies] = useState([]);
  const [country, setCountry, chosen, setChosen] = props;

  const choseCountry = (banana) => {
    fetch(`https://first-express-project.herokuapp.com/countries/${banana}`)
      .then((data) => data.json())

      .then((json) => {
        setChosen(json);
        console.log('hello');
        console.log(json);
        //   setCountry('');
        console.log(chosen);
      });
  };

  useEffect(() => {
    fetch(API)
      .then((res) => res.json())
      .then((json) => {
