import React, { useEffect, useState } from 'react';

//const data = 'https://first-express-project.herokuapp.com/movies';

const API = 'https://first-express-project.herokuapp.com/movies';

export const Movie = () => {
  const [movies, setMovies] = useState([]);
  const [country, setCountry] = useState([]);

  const onDetected = () => {
    fetch(`https://first-express-project.herokuapp.com/categories/${country}`)
      .then((data) => data.json())
      .then((json) => {
        setCountry(json);
      });
  };
  useEffect(() => {
    fetch(API)
      .then((res) => res.json())
      .then((json) => {
        setMovies(json);
      });
  }, []);

  return (
    <div>
      <div>
        <form
          onSubmit={(event) => {
            onDetected(country);
            event.preventDefault();
          }}
        >
          <label>
            <input
              required
              type='text'
              value={country}
              placeholder='Enter your country'
              onChange={(event) => setCountry(event.target.value)}
            />
          </label>

          <button className='input-button' type='submit'>
            search
          </button>
        </form>
        <div>{country && <h1>{country.title} </h1>}</div>
      </div>
      <p>{country.title}</p>
      <p>hello</p>
      {/* {movies.map((movie) => movie.title)} */}
      {movies.slice(0, 30).map((movie, index) => (
        <div className='movie' key={movie.id}>
          <h1>{movie.title} </h1>
          <p>{movie.description} </p>
        </div>
      ))}
    </div>
  );

  //
  //   {movies.slice(0, 30).map((movie, index) => (
  //     <div className='movie' key={movie.id}>
  //       <div>
  //         <h1>{movie.title} </h1>
  //         <h4>Released {movie.release_date} </h4>
  //       </div>
  //     </div>
  //   ))}
  //   )
  // </div>
  // );
};
