import React, { useEffect, useState } from 'react';

//const data = 'https://first-express-project.herokuapp.com/movies';

const API = 'https://first-express-project.herokuapp.com/movies';

export const Movie = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch('https://first-express-project.herokuapp.com/movies')
      .then((res) => res.json())
      .then((json) => {
        setMovies(json);
      });
  }, []);

  return (
    <div>
      <p>hello</p>
      {/* {movies.map((movie) => movie.title)} */}
      {movies.slice(0, 30).map((movie, index) => (
        <div className='movie' key={movie.id}>
          <h1>{movie.title} </h1>
          <p>{movie.description} </p>
        </div>
      ))}
    </div>
  );

