import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export const Category = () => {
  const [movies, setMovies] = useState([]);
  const [category, setCategory] = useState(['']);
  const [chosenCategory, setChosenCategory] = useState(['']);

  const choseCategory = (banana) => {
    fetch(`https://first-express-project.herokuapp.com/genres/${banana}`)
      .then((data) => data.json())

      .then((json) => {
        setChosenCategory(json);
        console.log('hello');
        console.log(json);
        //   setCountry('');
        console.log(chosenCategory);
      });
  };
  return (
    <div>
      <div>
        <Link to={`/`}>
          <div className='icon-container'>
            <div className='icon'>
              <i className='fas fa-chevron-circle-left'></i>
            </div>
          </div>
        </Link>
      </div>
      <form
        onSubmit={(event) => {
          choseCategory(category);
          event.preventDefault();
        }}
      >
        <label>
          <input
            required
            type='text'
            value={category}
            placeholder='Enter your Category'
            onChange={(event) => setCategory(event.target.value)}
          />
        </label>

        <button className='input-button' type='submit'>
          search
        </button>
      </form>
      {chosenCategory.length === 0 ? (
        <div>
          <p>
            Sorry, we couldn't find your genre. Please check your spelling or
            try another one.
          </p>
        </div>
      ) : (
        chosenCategory.map((item) => (
          <div>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
          </div>
        ))
      )}
    </div>
  );

  //
