import React, { useEffect, useState } from 'react';

//const data = 'https://first-express-project.herokuapp.com/movies';

const API = 'https://first-express-project.herokuapp.com/movies';

export const Movie = () => {
  const [movies, setMovies] = useState([]);
  const [country, setCountry] = useState([]);

  const choseCountry = () => {
    fetch(`https://first-express-project.herokuapp.com/countries/${country}`)
      .then((res) => res.json())
      .then((json) => {
        setCountry(json.results);
      });
  };

  useEffect(() => {
    fetch(API)
      .then((res) => res.json())
      .then((json) => {
        setMovies(json);
      });
  }, []);

  //  useEffect(() => {
  //   fetch(`https://first-express-project.herokuapp.com/movies/${country}`)
