import React, { useEffect, useState } from 'react';

export const Country = () => {
  const [country, setCountry] = useState([]);

  const onDetected = (country) => {
    fetch(`https://first-express-project.herokuapp.com/categories/${country}`)
      .then((data) => data.json())
      .then((json) => {
        setCountry(json);
      });
  };

  // useEffect(() => {
  //   fetch(`https://first-express-project.herokuapp.com/movies/${country}`)
  //     .then((res) => res.json())
  //     .then((json) => {
  //       console.log(json);
  //       setChosenCountry(json);
  //     });
  // }, [country]);

  return (
    <div>
      <form
        onSubmit={(event) => {
          onDetected(country);
          event.preventDefault();
        }}
      >
        <label>
          <input
            required
            type='text'
            value={country}
            placeholder='Enter your country'
            onChange={(event) => setCountry(event.target.value)}
          />
        </label>

        <button className='input-button' type='submit'>
          search
        </button>
      </form>
      <div>
        <h1>{country.title} </h1>
      </div>
      ))}
    </div>
  );
};
