  return (
    <main>
      <div>
        <h1>Welcome</h1>
        <h3>Looking for a movie?</h3>
        <h4>Search by</h4>
        <div>
          <Link to={`/Category/`}>
            <button>Category</button>
          </Link>
        </div>
        <h4>Search by</h4>
        <div>
          <Link to={`/Country/`}>
            <button>Country</button>
          </Link>
        </div>
        <h4>Search for a</h4>
        <div>
          <Link to={`/Movie/`}>
            <button>Specific movie</button>
          </Link>
        </div>
      </div>
      <div>
        {movies.slice(0, 30).map((movie, index) => (
          <div className='movie' key={movie.id}>
            <div>
              <h1>{movie.title} </h1>
              <h4>Released {movie.release_year} </h4>
            </div>
          </div>
        ))}
        )
      </div>
    </main>
  );

